public class FileController implements Controller {

    private static final Logger LOGGER = LoggerFactory.getLogger(FileController.class);
    public static final String TYPE = "type";
    public static final String CODEC = "codec";
    private final DefaultRawMetaParser metaParser;
    private final Map<String, Set<String>> params = new HashMap<String, Set<String>>();
    private RequestProcessor requestProcessor = new FileRequestProcessor();
    private static FileDiskInitializer fileDiskInitializer = new FileDiskInitializer();
    private String type;
    private String codec;
    private FileUpload fileUpload;

    public FileController() {
        super();
        metaParser = new DefaultRawMetaParser();
    }

    @Override
    public String control(HttpInfo httpInfo) throws Exception {
        long start = System.currentTimeMillis();
        HttpContent chunk = (HttpContent) httpInfo.getMsg();
        httpInfo.getDecoder().offer(chunk);
        readHttpDataChunkByChunk(httpInfo);
        boolean ready = StringUtils.isNotBlank(type) && !params.isEmpty() && fileUpload != null
                && httpInfo.getMsg() instanceof LastHttpContent;
        if (ready) {
            File file = saveFile();
            LOGGER.info("file upload time: " + (System.currentTimeMillis()-start));
            MetaInfo metaInfo = metaParser.parse(params);
            RawRequest rawRequest = new FileRequest(type, codec, metaInfo, file.getAbsolutePath(), file.getName() , 0);
            requestProcessor.process(rawRequest);
        }
        return HttpInfo.OK;
    }

    private void readHttpDataChunkByChunk(HttpInfo httpInfo) throws IOException {
        HttpPostRequestDecoder decoder = httpInfo.getDecoder();
        if (decoder != null) {
            while (decoder.hasNext()) {
                InterfaceHttpData data = decoder.next();
                if (data != null) {
                    readHttpData(data);
                }
            }
        }
    }

    private void readHttpData(InterfaceHttpData data) throws IOException {
        if (data.getHttpDataType() == InterfaceHttpData.HttpDataType.Attribute) {
            //meta info and type parameters
            Attribute attr = (Attribute) data;
            if (TYPE.equals(attr.getName())) {
                type = attr.getValue();
            } else if (CODEC.equals(attr.getName())) {
                codec = attr.getValue();
            } else {
                addParam(attr.getName(), attr.getValue());
            }
        } else {
            if (data.getHttpDataType() == InterfaceHttpData.HttpDataType.FileUpload) {
                FileUpload fileUpload = (FileUpload) data;
                if (fileUpload.isCompleted()) {
                    this.fileUpload = fileUpload;
                }
            }
        }
    }

    //FilePath: /logDir/type/date/filename.

private File saveFile() throws IOException {
        File dest;
        String dirPath = fileDiskInitializer.getDir(fileUpload.getFilename());
        dirPath += FileDiskInitializer.FILE_DELIMITER + type + FileDiskInitializer.FILE_DELIMITER + DateTime.now().toString("yyyy-MM-dd");
        File dir = new File(dirPath);
        boolean success = true;
        if (!dir.exists()) {
            success = dir.mkdirs();
        }
        if (success) {
            dest = new File(dirPath + FileDiskInitializer.FILE_DELIMITER + fileUpload.getFilename());
            fileUpload.renameTo(dest);
        } else {
            throw new IOException("dir: " + dir.getPath() + " create failed!

");
        }
        return dest;
    }

    private void addParam(String name, String value) {
        Set<String> values = params.get(name);
        if (values == null) {
            values = new HashSet<String>();
            values.add(value);
            params.put(name, values);
        } else {
            values.add(value);
        }
    }

}
